import LogoPL from "../../assets/images/PL.webp"
import { Link, NavLink } from "react-router-dom";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHome, faShieldAlt, faSearch } from "@fortawesome/free-solid-svg-icons";
import { motion } from "framer-motion";
import "./index.scss"
import { logoVariants } from "../../animations/elementAnimations";

const Sidebar = () => {
    const [isActive, setIsActive] = useState(false);
    
    const iconTextVariants = {
        hidden: { opacity: 0 },
        hover: { opacity: 1 }
    };

    return (
        <div className="nav-bar">
            <Link className="logo" to="/">
                <motion.img 
                    src={LogoPL} 
                    alt="Logo"
                    variants={logoVariants}
                    whileHover="hover"
                />
            </Link>
            <nav className={isActive ? "mobile-show" : ""}>
                <NavLink exact="true" activeclassname="active" to="/" onClick={() => setIsActive(false)}>
                    <motion.div className="icon-container">
                        <motion.div
                            initial={{ opacity: 1 }}
                            whileHover={{ opacity: 0 }}
                        >
                            <FontAwesomeIcon icon={faHome} />
                        </motion.div>
                        <motion.span
                            className="link-text"
                            variants={iconTextVariants}
                            initial="hidden"
                            whileHover="hover"
                        >
                            HOME
                        </motion.span>
                    </motion.div>
                </NavLink>
                
                <NavLink exact="true" activeclassname="active" className="teams-link" to="/teams" onClick={() => setIsActive(false)}>
                    <motion.div className="icon-container">
                        <motion.div
                            initial={{ opacity: 1 }}
                            whileHover={{ opacity: 0 }}
                        >
                            <FontAwesomeIcon icon={faShieldAlt} />
                        </motion.div>
                        <motion.span
                            className="link-text"
                            variants={iconTextVariants}
                            initial="hidden"
                            whileHover="hover"
                        >
                            TEAMS
                        </motion.span>
                    </motion.div>
                </NavLink>
                
                <NavLink exact="true" activeclassname="active" className="search-link" to="/search" onClick={() => setIsActive(false)}>
                    <motion.div className="icon-container">
                        <motion.div
                            initial={{ opacity: 1 }}
                            whileHover={{ opacity: 0 }}
                        >
                            <FontAwesomeIcon icon={faSearch} />
                        </motion.div>
                        <motion.span
                            className="link-text"
                            variants={iconTextVariants}
                            initial="hidden"
                            whileHover="hover"
                        >
                            SEARCH
                        </motion.span>
                    </motion.div>
                </NavLink>
            </nav>
            
            <motion.div 
                className={`hamburger-icon ${isActive ? 'open' : ''}`}
                onClick={() => setIsActive(!isActive)}
                whileTap={{ scale: 0.9 }}
            >
                <span></span>
                <span></span>
                <span></span>
            </motion.div>
        </div>
    )
}

export default Sidebar;
