import { useState, useEffect } from "react";
import Loader from "react-loaders";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import { letterContainerVariants, letterVariants } from "../../animations/textAnimations";
import { fadeInVariants } from "../../animations/elementAnimations";
import { cardContainerVariants, cardVariants } from "../../animations/cardAnimations";
import { buttonVariants } from "../../animations/elementAnimations";
import './index.scss';

// Sample data for testing until we fix the import
const sampleTeams = [
  { 
    id: 1, 
    name: "Arsenal", 
    logo: "https://resources.premierleague.com/premierleague/badges/t3.svg"
  },
  { 
    id: 2, 
    name: "Manchester United", 
    logo: "https://resources.premierleague.com/premierleague/badges/t1.svg" 
  },
  { 
    id: 3, 
    name: "Liverpool", 
    logo: "https://resources.premierleague.com/premierleague/badges/t14.svg" 
  },
  { 
    id: 4, 
    name: "Chelsea", 
    logo: "https://resources.premierleague.com/premierleague/badges/t8.svg" 
  }
];

const Team = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredTeams, setFilteredTeams] = useState(sampleTeams);

    useEffect(() => {
        // Using the sample data for now
        const filtered = sampleTeams.filter(team =>
            team.name.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setFilteredTeams(filtered);
        
        /* Once you fix your JSON file, you can uncomment this:
        try {
            // Import dynamically to handle errors
            import("../../data/teams.json").then(module => {
                const teamsData = module.default.teams || module.default;
                const filtered = teamsData.filter(team =>
                    team.name.toLowerCase().includes(searchTerm.toLowerCase())
                );
                setFilteredTeams(filtered);
            }).catch(error => {
                console.error("Failed to load teams data:", error);
            });
        } catch (error) {
            console.error("Error in teams component:", error);
        }
        */
    }, [searchTerm]);

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    }
    
    return (
        <>
            <div className="container teams-page">
                <motion.h1 
                    className="page-title"
                    variants={letterContainerVariants}
                    initial="hidden"
                    animate="visible"
                >
                    {"Teams".split("").map((letter, i) => (
                        <motion.span
                            key={i}
                            variants={letterVariants}
                            whileHover="hover"
                        >
                            {letter}
                        </motion.span>
                    ))}
                </motion.h1>

                <motion.div 
                    className="search-bar"
                    variants={fadeInVariants}
                    initial="hidden"
                    animate="visible"
                    custom={0.5}
                >
                    <input
                        type="text"
                        placeholder="Search for teams"
                        value={searchTerm}
                        onChange={handleSearchChange}
                    />
                </motion.div>

                <motion.div 
                    className="teams-container"
                    variants={cardContainerVariants}
                    initial="hidden"
                    animate="visible"
                >
                    {filteredTeams.map((team, idx) => (
                        <motion.div 
                            key={idx}
                            className="team-card"
                            variants={cardVariants}
                            whileHover="hover"
                        >
                            <img src={team.logo} alt={team.name} />
                            <h3>{team.name}</h3>
                            <motion.div 
                                whileHover="hover"
                                variants={buttonVariants}
                                className="btn-container"
                            >
                                <Link to={`/teams/${team.id}`}>View Team</Link>
                            </motion.div>
                        </motion.div>
                    ))}
                </motion.div>
            </div>
            <Loader type="pacman" />
        </>
    );
}

export default Team;
